  selector: 'app-poke-card',
  templateUrl: './poke-card.component.html',
  styleUrls: ['./poke-card.component.scss'],
})
export class PokeCardComponent implements OnChanges {
  @Input() pokemon?: IPokemon;

  ngOnChanges(change: SimpleChanges) {
    console.log(change);
  }
}
